import React, { useEffect, useState } from 'react';
import Image1 from 'assets/image1.png';

function SlideShow() {
  const images: Image[] = Array.from({ length: 10 }, (_, index) => ({
    src: require(`assets/image${index + 1}.png`).default,
    alt: `Image ${index + 1}`,
  }));

  const delay = async (ms: number) => {
    return new Promise((resolve) => setTimeout(resolve, ms));
  };

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(async () => {
      await delay(1000);
      console.log(currentImageIndex);
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 2000);

    return () => clearInterval(intervalId);
  }, [currentImageIndex, images.length]);

  return (
    <>
      <div>
        <img src={images[currentImageIndex].src} alt='' style={{ width: '500px', height: '500px', margin: '5px' }} />
        <img src={Image1} alt='' style={{ width: '500px', height: '500px', margin: '5px' }} />
      </div>
    </>
  );
}

export default SlideShow;
